import { useState } from "react";
import axios from "axios";

const [appointments, setAppointments] = useState([]);

const fetchAppointments = async () => {
  try {
    const response = await axios.get("http://127.0.0.1:8000/appointments/");
    setAppointments(response.data);
  } catch (error) {
    console.log(error);
  }
};


function App() {
  const [userId, setUserId] = useState("");
  const [date, setDate] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");

  const handleBook = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/appointments/", {
        user_id: parseInt(userId),
        start_time: `${date}T${startTime}:00`,
        end_time: `${date}T${endTime}:00`
      });
      alert("✅ Appointment Booked!");
    } catch (error) {
      alert("❌ Error while booking appointment");
      console.log(error);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <br /><br />
<hr />
<br />

<button onClick={fetchAppointments}>Load Appointments</button>

<table border="1" cellPadding="8" style={{ marginTop: 20 }}>
  <thead>
    <tr>
      <th>ID</th>
      <th>User ID</th>
      <th>Start Time</th>
      <th>End Time</th>
      <th>Status</th>
    </tr>
  </thead>
  <tbody>
    {appointments.map((a) => (
      <tr key={a.id}>
        <td>{a.id}</td>
        <td>{a.user_id}</td>
        <td>{a.start_time}</td>
        <td>{a.end_time}</td>
        <td>{a.status}</td>
      </tr>
    ))}
  </tbody>
</table>

    </div>
  );
}

export default App;
