import React, { useState } from "react";
import axios from "axios";

function App() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [name, setName] = useState("");

  const [loggedInUserId, setLoggedInUserId] = useState(localStorage.getItem("user_id") || null);
  const [token, setToken] = useState(localStorage.getItem("token") || null);

  const [date, setDate] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");

  const [appointments, setAppointments] = useState([]);

  // SIGNUP
  const handleSignup = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/signup/", null, {
        params: { name, email, password }
      });
      alert("✅ Signup successful, now login");
    } catch (error) {
      alert("❌ Signup failed");
      console.log(error);
    }
  };

  // LOGIN
  const handleLogin = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/login/", null, {
        params: { email, password }
      });

      const { token, user_id } = response.data;
      setLoggedInUserId(user_id);
      setToken(token);

      localStorage.setItem("user_id", user_id);
      localStorage.setItem("token", token);

      alert("✅ Login successful");
    } catch (error) {
      alert("❌ Login failed");
      console.log(error);
    }
  };

  // BOOK APPOINTMENT (uses logged-in user)
  const handleBook = async () => {
    if (!loggedInUserId) {
      alert("Please login first");
      return;
    }
    try {
      await axios.post("http://127.0.0.1:8000/appointments/", {
        user_id: parseInt(loggedInUserId),
        start_time: `${date}T${startTime}:00`,
        end_time: `${date}T${endTime}:00`,
      });
      alert("✅ Appointment Booked!");
    } catch (error) {
      alert("❌ Error while booking appointment");
      console.log(error);
    }
  };

  // LOAD ALL APPOINTMENTS
  const fetchAppointments = async () => {
  const response = await axios.get("http://127.0.0.1:8000/my-appointments/", {
    params: { user_id: loggedInUserId }
  });
  setAppointments(response.data);
};


  return (
    <div style={{ padding: 20 }}>
      {!loggedInUserId ? (
        <>
          <h2>Signup</h2>
          <input placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} /><br /><br />
          <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} /><br /><br />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} /><br /><br />
          <button onClick={handleSignup}>Signup</button>

          <br /><br /><hr /><br />

          <h2>Login</h2>
          <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} /><br /><br />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} /><br /><br />
          <button onClick={handleLogin}>Login</button>
        </>
      ) : (
        <>
          <h3>Logged in as User ID: {loggedInUserId}</h3>

          <h2>Book Appointment</h2>
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} /><br /><br />
          <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} /><br /><br />
          <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} /><br /><br />
          <button onClick={handleBook}>Book Appointment</button>

          <br /><br />
          <hr />
          <br />

          <button onClick={fetchAppointments}>Load Appointments</button>

          <table border="1" cellPadding="8" style={{ marginTop: 20 }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>User ID</th>
                <th>Start Time</th>
                <th>End Time</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((a) => (
                <tr key={a.id}>
                  <td>{a.id}</td>
                  <td>{a.user_id}</td>
                  <td>{a.start_time}</td>
                  <td>{a.end_time}</td>
                  <td>{a.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
