import React, { useState } from "react";
import axios from "axios";

function App() {
  const savedToken = localStorage.getItem("token");

  const api = axios.create({
    baseURL: "http://127.0.0.1:8000",
    headers: {
      Authorization: `Bearer ${savedToken}`
    }
  });

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");

  const [loggedInUserId, setLoggedInUserId] = useState(localStorage.getItem("user_id") || null);
  const [token, setToken] = useState(savedToken || null);

  const [date, setDate] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");

  const [appointments, setAppointments] = useState([]);

  // EVENT STATE (SlotSwapper)
  const [eventTitle, setEventTitle] = useState("");
  const [events, setEvents] = useState([]);
  const [swappableSlots, setSwappableSlots] = useState([]);

  // SIGNUP
  const handleSignup = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/signup/", null, {
        params: { name, email, password }
      });
      alert("✅ Signup successful, now login");
    } catch (error) {
      alert("❌ Signup failed");
    }
  };

  // LOGIN
  const handleLogin = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/login/", null, {
        params: { email, password }
      });

      const { token, user_id } = response.data;

      localStorage.setItem("user_id", user_id);
      localStorage.setItem("token", token);

      setLoggedInUserId(user_id);
      setToken(token);

      alert("✅ Login successful");
      window.location.reload();
    } catch {
      alert("❌ Login failed");
    }
  };

  const handleLogout = () => {
  localStorage.clear();
  setLoggedInUserId(null);
  setToken(null);
  alert("Logged out ✅");
  window.location.reload();
};


  // BOOK APPOINTMENT
  const handleBook = async () => {
    try {
      await api.post("/appointments/", {
        start_time: `${date}T${startTime}:00`,
        end_time: `${date}T${endTime}:00`,
      });
      alert("✅ Appointment booked!");
    } catch {
      alert("❌ Error booking appointment");
    }
  };

  // LOAD APPOINTMENTS
  const fetchAppointments = async () => {
    const res = await api.get("/my-appointments/");
    setAppointments(res.data);
  };

  // CREATE EVENT
  const handleCreateEvent = async () => {
    try {
      await api.post("/events/", {
        title: eventTitle,
        start_time: `${date}T${startTime}:00`,
        end_time: `${date}T${endTime}:00`
      });
      alert("✅ Event Created!");
    } catch {
      alert("❌ Failed to create event");
    }
  };

  // LOAD MY EVENTS
  const fetchEvents = async () => {
    const res = await api.get("/events/my");
    setEvents(res.data);
  };

  // MAKE EVENT SWAPPABLE
  const makeSwappable = async (id) => {
    await api.patch(`/events/${id}/status`, { status: "SWAPPABLE" });
    fetchEvents();
  };

  // LOAD MARKETPLACE
  const fetchSwappableSlots = async () => {
    const res = await api.get("/swappable-slots");
    setSwappableSlots(res.data);
  };

  return (
    <div style={{ padding: 20 }}>
      {!loggedInUserId ? (
        <>
          <h2>Signup</h2>
          <input placeholder="Name" onChange={(e) => setName(e.target.value)} /><br /><br />
          <input placeholder="Email" onChange={(e) => setEmail(e.target.value)} /><br /><br />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} /><br /><br />
          <button onClick={handleSignup}>Signup</button>

          <br /><hr /><br />

          <h2>Login</h2>
          <input placeholder="Email" onChange={(e) => setEmail(e.target.value)} /><br /><br />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} /><br /><br />
          <button onClick={handleLogin}>Login</button>
        </>
      ) : (
        <>
          <h3>Logged in as: User {loggedInUserId}</h3>
          <h3>Logged in as: User {loggedInUserId}</h3>
<button onClick={handleLogout}>Logout</button>
<br /><br />


          {/* APPOINTMENTS */}
          <h2>Book Appointment</h2>
          <input type="date" onChange={(e) => setDate(e.target.value)} /><br /><br />
          <input type="time" onChange={(e) => setStartTime(e.target.value)} /><br /><br />
          <input type="time" onChange={(e) => setEndTime(e.target.value)} /><br /><br />
          <button onClick={handleBook}>Book Appointment</button>

          <br /><button onClick={fetchAppointments}>Load My Appointments</button>

          <table border="1" cellPadding="8" style={{ marginTop: 20 }}>
            <thead><tr><th>ID</th><th>Start</th><th>End</th><th>Status</th></tr></thead>
            <tbody>
              {appointments.map(a => (
                <tr key={a.id}>
                  <td>{a.id}</td>
                  <td>{a.start_time}</td>
                  <td>{a.end_time}</td>
                  <td>{a.status}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <br /><hr /><br />

          {/* EVENTS */}
          <h2>Create Event (SlotSwapper)</h2>
          <input placeholder="Event Title" onChange={(e) => setEventTitle(e.target.value)} /><br /><br />
          <input type="date" onChange={(e) => setDate(e.target.value)} /><br /><br />
          <input type="time" onChange={(e) => setStartTime(e.target.value)} /><br /><br />
          <input type="time" onChange={(e) => setEndTime(e.target.value)} /><br /><br />
          <button onClick={handleCreateEvent}>Create Event</button>

          <br /><button onClick={fetchEvents}>Load My Events</button>

          <table border="1" cellPadding="8" style={{ marginTop: 20 }}>
            <thead><tr><th>ID</th><th>Title</th><th>Status</th><th>Action</th></tr></thead>
            <tbody>
              {events.map(e => (
                <tr key={e.id}>
                  <td>{e.id}</td>
                  <td>{e.title}</td>
                  <td>{e.status}</td>
                  <td>
                    {e.status === "BUSY" && (
                      <button onClick={() => makeSwappable(e.id)}>Make Swappable</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <br /><hr /><br />

          {/* MARKETPLACE */}
          <h2>Marketplace (Other Users' Swappable Slots)</h2>
          <button onClick={fetchSwappableSlots}>Show Marketplace</button>

          <table border="1" cellPadding="8" style={{ marginTop: 20 }}>
            <thead><tr><th>ID</th><th>User</th><th>Title</th><th>Start</th><th>End</th></tr></thead>
            <tbody>
              {swappableSlots.map(s => (
    <tr key={s.id}>
      <td>{s.id}</td>
      <td>{s.user_id}</td>
      <td>{s.title}</td>
      <td>{s.start_time}</td>
      <td>{s.end_time}</td>
      <td>
        <button onClick={() => selectSwapTarget(s.id)}>Request Swap</button>
      </td>
    </tr>
  ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
